# configuration file /etc/nginx/modules-enabled/50-mod-rtmp.conf:
load_module modules/ngx_rtmp_module.so;

# configuration file /etc/nginx/rtmp.conf:
rtmp_auto_push on;

rtmp {
    server {
        listen 1935;
        chunk_size 4096;

        # Live Streaming Application - OBS ingestion
        # Proxies to rtmp-receiver on port 1936 for multi-quality transcoding
        application live {
            live on;
            record off;

            # Allow publishing from anywhere (auth handled by rtmp-receiver)
            allow publish all;
            allow play all;

            # Push to rtmp-receiver for auth validation and transcoding
            push rtmp://localhost:1936/live;
        }

        # VOD Application - Channel 1
        application ch1-vod {
            live on;
            record off;

            # Allow publishing from localhost only (security)
            allow publish 127.0.0.1;
            deny publish all;

            # Allow playback from anywhere
            allow play all;

            # Native HLS transcoding
            hls on;
            hls_path /var/www/hls/ch1-vod;
            hls_fragment 3s;
            hls_playlist_length 60s;
        }

        # Commercial Application - Channel 1
        application ch1-comm {
            live on;
            record off;

            # CRITICAL: Keep stream alive even without publisher
            idle_streams on;

            # Auto-drop publishers that stop sending data
            drop_idle_publisher 10s;

            # Allow publishing from localhost only (security)
            allow publish 127.0.0.1;
            deny publish all;

            # Allow playback from anywhere
            allow play all;

            # Native HLS transcoding
            hls on;
            hls_path /var/www/hls/ch1-comm;
            hls_fragment 6s;
            hls_playlist_length 60s;
        }
    }
}

